	/**
	 * 获取总记录数
	 * @param conditions
	 * @return
	 */
	public int totalLevels(String conditions){
		
		StringBuffer sql = new StringBuffer() ;
		sql.append("select count(*)") ;
		sql.append(" from " + tableName) ;
		sql.append(" where 1=1") ;
		int total = 0 ;
		
		if(conditions != null && !conditions.equals("")){
			sql.append(conditions) ;
		}
		
		Connection conn = DBConnection.instance.getConnection() ;
		Statement st = null ;
		ResultSet rs = null ;
		
		try{
			st = conn.createStatement() ;
			rs = st.executeQuery(sql.toString()) ;
			
			if(rs == null){
				return total ;
			}
			
			while(rs.next()){
				total = rs.getInt(1) ;
			}
		}catch(Exception e){
			e.printStackTrace() ;
			return 0 ;
		}finally{
			DBConnection.close(rs) ;
			DBConnection.close(st) ;
			DBConnection.close(conn) ;
		}
		return total ;
	}
	

	/**
	 * 更新信息
	 * @param userinfo
	 * @return
	 */
	public boolean update(DiAwardLevelBean levelbean){
		
		Connection conn = DBConnection.instance.getConnection() ;
		boolean flag = false ;
		String field = "AwardLevel" ;
		
		try{
			flag = DAOUtil.update(levelbean, tableName, "IndexId", field, conn) ;
		}catch(Exception e){
			e.printStackTrace() ;
			 return false ;
		}
		
		return flag ;
	}
	
	/**
	 * 删除
	 * @param ids
	 * @return
	 */
	public boolean deleteByIds(String ids){
		
		StringBuffer sql = new StringBuffer() ;
		sql.append("delete from " + tableName) ;
		sql.append(" where IndexID  in " + ids) ;
		int flag = 0 ;
		Connection conn = DBConnection.instance.getConnection() ;
		Statement st = null ;
		
		try{
			st = conn.createStatement() ;
			flag = st.executeUpdate(sql.toString()) ;
		}catch(Exception e){
			e.printStackTrace() ;
			return false ;
		}finally{
			DBConnection.close(st) ;
			DBConnection.close(conn) ;
		}
		
		if(flag > 0){
			return true ;
		}else{
			return false ;
		}
		
	}
	
	
	/**
	 * 判断中是否已包含该数据
	 */
	public boolean hasLevel(String levelID){
		String sql = "select * from " + tableName + " where IndexID='"+ levelID	+ "'";
		Connection conn = DBConnection.instance.getConnection();
		Statement st = null;
		ResultSet rs = null;
		boolean flag = false;
		try{
			st = conn.createStatement();
			rs = st.executeQuery(sql);
			
			while(rs.next()){
				flag = true;
			}
		}catch(Exception e){
			e.printStackTrace() ;
			return false ;
		}finally{
			DBConnection.close(rs);
			DBConnection.close(st);	
			DBConnection.close(conn);
		}
		return flag;
	}

